import{copy}from"./_copy.js";function copyFunction(){document.querySelector(".a_guide-detailView").querySelectorAll(".l_btn").forEach(t=>{t.addEventListener("click",function(){var t,o=this.closest(".l_contents").getAttribute("data-copy");this.classList.contains("copyHTML")?(t=copy[o][0].HTML_PC,window.navigator.clipboard.writeText(t)):this.classList.contains("copyCSS")?(t=copy[o][0].SCSS_PC,window.navigator.clipboard.writeText(t)):this.classList.contains("copyJS")&&(t=copy[o][0].JS,window.navigator.clipboard.writeText(t)),clearTimeout();const c=document.querySelector(".copyPop");c.classList.add("active"),setTimeout(()=>{c.classList.remove("active")},1e3)})})}export{copyFunction};